    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        // TODO add your handling code here:
     try {
            double inp = Double.parseDouble(jTextField9.getText());//gettinh input
            int frm = jComboBox9.getSelectedIndex(); //converting form 
            int to = jComboBox10.getSelectedIndex();// converting to
            double res = inp; //result
        //calculation
            switch (frm) {
                case 0:
                    
                    switch (to) {
                        case 1:
                            res = 5.0 * (inp - 32.0) / 9.0; 
                            break;
                        case 2:
                            res = 273 + (5.0 * (inp - 32.0) / 9.0);
                            break;
                    }
                    break;
                case 1:
                    switch (to) {
                        case 0:
                            res = (9.0 * inp / 5.0) + 32.0;
                            break;
                        case 2:
                            res = inp + 273;
                            break;
                    }
                    break;
                case 2:
                    switch (to) {
                        case 0:
                            res = (9.0 * (inp - 273) / 5.0) + 32.0;
                            break;
                        case 1:
                            res = inp - 273;
                            break;
                    }
                    break;

            }

            String out =Double.toString(res);
            jTextField10.setText(out);
             jTextField10.setEditable(false);

        } catch (Exception ex) {
            jTextField10.setText("Invalid");
             jTextField10.setEditable(false);
        }
    }//GEN-LAST:event_jButton6ActionPerformed

