    /**
     * 
     * @param url
     * @return
     * @throws Exception 
     */
    public String ConvertCurrency(String url) throws Exception {

        String text = "", nl;
        Scanner sc = new Scanner(new URL(url).openStream());

        while (sc.hasNext()) {
            nl = sc.nextLine(); 
            if (nl.startsWith("<div id=currency_converter_result>")) {
                text = nl;
                break;
            }
        }

        text = text.replace("<div id=currency_converter_result>", "");
        text = text.replace("<span class=bld>", "");
        text = text.replace("</span>", " ");

        return text;
    }

    
    
